if (targets.length > 0) {
	targets.forEach(function(element, index) {
		ACTIONSHEET_JUMP_TO.addItem(element.key,element.value);
	});
} else {
	ACTIONSHEET_JUMP_TO.addItem("",TEXT_POOL.NoJumpTargets);
