var lExport = ACTIONSHEET_EXPORT_ACTIONS.getSelectedValue();
if (lExport === "EXCEL_STANDARD") {
	g_exportType = ExportType.EXCEL_xlsx;
	if (XEXCEL_VERSION) { // use Excel Version defined in XEXCEL_VERSION
		g_exportVersion = "";
	} else { // use default Excel Version
		g_exportVersion = "4.10";
	}
} else if (lExport === "EXCEL_BASIC") {
	g_exportType = ExportType.EXCEL_xlsx;
	g_exportVersion = "0.00"; // use initial Excel Version
}
var lError = APPLICATION.isDataSourceTooLarge();
if (lError) {
	DIALOG_EXPORT.open();
} else {
	GLOBAL_SCRIPT_ACTIONS.ApplyExportSettings(g_exportType, g_exportVersion);
}
